import React, { useEffect, useState } from "react";
import Container from "react-bootstrap/Container";
import { Link } from "react-router-dom";
import { fetchPosts } from "../api/apiClient";
import "../styles/PostListing.css";

function PostListing() {
  const [posts, setPosts] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const loadPosts = async () => {
      try {
        const data = await fetchPosts();
        setPosts(data);
        setLoading(false);
      } catch (error) {
        console.error("Error loading posts:", error);
        setLoading(false);
      }
    };

    loadPosts();
  }, []);

  if (loading) {
    return <div>Loading posts...</div>;
  }

  if (posts.length === 0) {
    return <div>No posts available.</div>;
  }

  return (
    <Container className="mt-5">
      <div id="topicList">
        {posts.map((post) => (
          <Link to={`/posts/${post.id}`} key={post.id}>
            <div class="p-3">
              <div class="card">
                <div class="mx-4 my-2">
                  <div class="d-flex justify-content-between align-items-center mt-2 mb-2">
                    <h5 class="fw-bold mb-0">{post.title}</h5>
                    <span class="badge bg-secondary">
                      {post.commentCounter} {comment}
                    </span>
                  </div>
                  <div class="description-wrapper mb-2">
                    <p class="description-preview mb-2">{post.description}</p>
                  </div>
                </div>
              </div>
            </div>
          </Link>
        ))}
      </div>
    </Container>
  );
}

export default PostListing;
