import React, { useState, useEffect, useRef } from "react";
import { Link } from "react-router-dom";
import "../styles/PostPreview.css"; // Ensure your CSS is imported

const PostPreview = ({ post }) => {
  const [applyFade, setApplyFade] = useState(false);
  const previewRef = useRef(null);

  useEffect(() => {
    const element = previewRef.current;
    const lineHeight = parseFloat(window.getComputedStyle(element).lineHeight);
    const maxHeight = lineHeight * 3; // Adjust for 3 lines
    setApplyFade(element.scrollHeight > maxHeight);
  }, [post.description]);

  return (
    <Link to={`/post/${post._id}`} style={{ textDecoration: "none" }}>
      <div className="my-5">
        <div className="card card-hover-effect">
          <div className="mx-4 my-2">
            <div className="d-flex justify-content-between align-items-center my-2">
              <h3 className="fw-bold mb-0">{post.title}</h3>
            </div>
            <p>
              @{post.author} | {post.date}
            </p>
            <div
              className={`description-wrapper ${
                applyFade ? "fade-effect" : ""
              }`}
            >
              <p className="description-preview mb-2" ref={previewRef}>
                {post.description}
              </p>
            </div>
            <div className="my-1">
              <span className="fw-bold fs-6 badge bg-secondary rounded-pill me-1">
                {/* Like icon */}
                <svg
                  xmlns="http://www.w3.org/2000/svg"
                  width="16"
                  height="16"
                  fill="currentColor"
                  className="bi bi-hand-thumbs-up-fill me-1"
                  viewBox="0 0 16 16"
                >
                  {/* SVG path for like icon */}
                  <path d="..." />
                </svg>
                {post.likes + " | "}
                {/* Dislike icon */}
                <svg
                  xmlns="http://www.w3.org/2000/svg"
                  width="16"
                  height="16"
                  fill="currentColor"
                  className="bi bi-hand-thumbs-down-fill me-1"
                  viewBox="0 0 16 16"
                >
                  {/* SVG path for dislike icon */}
                  <path d="..." />
                </svg>
                {post.dislikes}
              </span>
              <span className="fw-bold fs-6 badge bg-secondary rounded-pill">
                {/* Comment icon */}
                <svg
                  xmlns="http://www.w3.org/2000/svg"
                  width="15"
                  height="15"
                  fill="currentColor"
                  className="bi bi-chat-square-text-fill me-1"
                  viewBox="0 0 16 16"
                >
                  <svg
                    xmlns="http://www.w3.org/2000/svg"
                    width="15"
                    height="15"
                    fill="currentColor"
                    className="bi bi-chat-square-text-fill me-1"
                    viewBox="0 0 16 16"
                  >
                    <path d="M0 2a2 2 0 0 1 2-2h12a2 2 0 0 1 2 2v8a2 2 0 0 1-2 2h-2.5a1 1 0 0 0-.8.4l-1.9 2.533a1 1 0 0 1-1.6 0L5.3 12.4a1 1 0 0 0-.8-.4H2a2 2 0 0 1-2-2zm3.5 1a.5.5 0 0 0 0 1h9a.5.5 0 0 0 0-1zm0 2.5a.5.5 0 0 0 0 1h9a.5.5 0 0 0 0-1zm0 2.5a.5.5 0 0 0 0 1h5a.5.5 0 0 0 0-1z" />
                  </svg>{" "}
                  <path d="..." />
                </svg>
                {post.commentCounter}
              </span>
            </div>
          </div>
        </div>
      </div>
    </Link>
  );
};

export default PostPreview;
