import React, { useEffect, useState } from "react";
import Container from "react-bootstrap/Container";
import { Link } from "react-router-dom";
import { fetchPosts } from "../api/apiClient";
import "../styles/PostListing.css";

function PostListing() {
  const [posts, setPosts] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const loadPosts = async () => {
      try {
        const data = await fetchPosts();
        setPosts(data);
        setLoading(false);
      } catch (error) {
        console.error("Error loading posts:", error);
        setLoading(false);
      }
    };

    loadPosts();
  }, []);

  if (loading) {
    return <div>Loading posts...</div>;
  }

  if (posts.length === 0) {
    return <div>No posts available.</div>;
  }

  return (
    <Container className="mt-5">
      <div id="topicList">
        {posts.map((post) => (
          <Link to={`/post/${post._id}`} key={post._id}>
            <div className="my-5">
              <div className="card">
                <div className="mx-4 my-2">
                  <div className="d-flex justify-content-between align-items-center my-2">
                    <h3 className="fw-bold mb-0">{post.title}</h3>
                  </div>
                  <div className="description-wrapper mb-2">
                    <p className="description-preview mb-2">
                      {post.description}
                    </p>
                  </div>
                  <span className="fw-bold badge bg-secondary rounded-pill">
                    <svg
                      xmlns="http://www.w3.org/2000/svg"
                      width="16"
                      height="16"
                      fill="currentColor"
                      class="bi bi-emoji-heart-eyes"
                      viewBox="0 0 16 16"
                    >
                      <path d="M8 15A7 7 0 1 1 8 1a7 7 0 0 1 0 14zm0 1A8 8 0 1 0 8 0a8 8 0 0 0 0 16z" />
                      <path d="M11.315 10.014a.5.5 0 0 1 .548.736A4.498 4.498 0 0 1 7.965 13a4.498 4.498 0 0 1-3.898-2.25.5.5 0 0 1 .548-.736h.005l.017.005.067.015.252.055c.215.046.515.108.857.169.693.124 1.522.242 2.152.242.63 0 1.46-.118 2.152-.242a26.58 26.58 0 0 0 1.109-.224l.067-.015.017-.004.005-.002zM4.756 4.566c.763-1.424 4.02-.12.952 3.434-4.496-1.596-2.35-4.298-.952-3.434zm6.488 0c1.398-.864 3.544 1.838-.952 3.434-3.067-3.554.19-4.858.952-3.434z" />
                    </svg>
                    {post.commentCounter}
                  </span>
                </div>
              </div>
            </div>
          </Link>
        ))}
      </div>
    </Container>
  );
}

export default PostListing;
