import React, { useEffect, useState } from "react";
import { useParams, Link } from "react-router-dom";
import { fetchPostById, addComment } from "../api/apiClient";
import { Form, Button } from "react-bootstrap";
import "../styles/Post.css";

function Post() {
  const [post, setPost] = useState(null);
  const [commentText, setCommentText] = useState("");
  const { id } = useParams();

  useEffect(() => {
    fetchPostById(id)
      .then((data) => setPost(data))
      .catch((error) => console.error(error));
  }, [id]);

  const handleCommentSubmit = async () => {
    if (!commentText.trim()) {
      // Optional: Add error handling for empty comment
      return;
    }

    try {
      const newComment = {
        author: "Username", // Replace with dynamic username if available
        description: commentText,
      };
      const updatedPost = await addComment(id, newComment);
      setPost(updatedPost); // Update the post with the new comment
      setCommentText(""); // Clear the comment text area
    } catch (error) {
      console.error("Error adding comment:", error);
      // Optional: Display error message to user
    }
  };

  if (!post) {
    return <div>Loading post...</div>;
  }

  return (
    <div className="container mt-5">
      <div className="my-5">
        <Link to="/" className="btn btn-secondary">
          Back To Topics
        </Link>
      </div>
      <h2>{post.title}</h2>
      <p>{post.description}</p>
      <hr />
      <h4>Comments</h4>
      <ul className="list-group mt-3">
        {post.comments.map((comment, index) => (
          <li key={index} className="list-group-item">
            <div className="fw-bold">@{comment.author}</div>
            {comment.description}
          </li>
        ))}
      </ul>
      <div className="mt-4">
        <Form.Group>
          <Form.Label>Author</Form.Label>
          <Form.Control placeholder="Type your name here..."></Form.Control>
        </Form.Group>
        <Form.Group>
          <Form.Label>Add a Comment</Form.Label>
          <Form.Control
            as="textarea"
            placeholder="Type your comment here..."
            rows={3}
            className="my-3"
            onChange={(e) => setCommentText(e.target.value)}
            required
          ></Form.Control>
        </Form.Group>
        <Button id="submitComment" type="button" onClick={handleCommentSubmit}>
          Submit
        </Button>
      </div>
    </div>
  );
}

export default Post;
